import React, { useEffect, useState } from "react";

function Home() {
  const [currencyData, setCurrencyData] = useState({
    base: "USD",
    rates: { USD: "—", GBP: "—", PKR: "—" },
  });

  useEffect(() => {
    fetch("http://127.0.0.1:5000/currency")
      .then((res) => res.json())
      .then((data) => {
        if (data && data.rates) {
          setCurrencyData({
            base: data.base,
            rates: {
              USD: data.rates.USD,
              GBP: data.rates.GBP,
              PKR: data.rates.PKR,
            },
          });
        }
      })
      .catch((err) => console.error("Error fetching currency data:", err));
  }, []);

  return (
    <div className="container">
      <h1>Currency Rates</h1>
      <p><strong>Base Currency:</strong> {currencyData.base}</p>
      <div>
        <p><strong>USD:</strong> {currencyData.rates.USD !== "—" ? currencyData.rates.USD : "Loading..."}</p>
        <p><strong>GBP:</strong> {currencyData.rates.GBP !== "—" ? currencyData.rates.GBP : "Loading..."}</p>
        <p><strong>PKR:</strong> {currencyData.rates.PKR !== "—" ? currencyData.rates.PKR : "Loading..."}</p>
      </div>
    </div>
  );
}

export default Home;
