import React, { useState, useEffect } from "react";
import "../styles/Home.css";
import { FaLinkedin, FaGithub, FaInstagram } from "react-icons/fa";

const Home = () => {
  // Split the sentence into normal + highlight parts
  const beforeText = "And I'm a ";
  const highlightText = "Full Stack Developer and ML Engineer";

  const [displayedText, setDisplayedText] = useState("");
  const [highlighted, setHighlighted] = useState(false);
  const [showCursor, setShowCursor] = useState(true);

  useEffect(() => {
    const fullText = beforeText + highlightText;
    let index = 0;

    const typingInterval = setInterval(() => {
      if (index < fullText.length) {
        setDisplayedText((prev) => prev + fullText.charAt(index));
        index++;
      } else {
        clearInterval(typingInterval);
        // Remove cursor after typing finishes
        setTimeout(() => setShowCursor(false), 800);
      }
    }, 100); // typing speed

    return () => clearInterval(typingInterval);
  }, []);

  return (
    <div className="home-container">
      {/* Hero Section */}
      <div className="hero">
        <div className="hero-left">
          <img src="/me3.jpeg" alt="Profile" className="profile-img" />
        </div>

        <div className="hero-right">
          <h3>Welcome, Myself</h3>
          <h1>M. Talha Faizan</h1>
          <h2>
            <span className="typewriter">
              {/* Before normal text */}
              {displayedText.slice(0, beforeText.length)}

              {/* Highlighted text */}
              <span className="highlight">
                {displayedText.slice(beforeText.length)}
              </span>

              {/* Cursor */}
              {showCursor && <span className="cursor">|</span>}
            </span>
          </h2>
          <p>
            I am a passionate Web Developer and have a keen interest in AI/ML
            Engineering. I love building creative and efficient solutions that
            bridge the gap between modern web technologies and intelligent
            systems. Always eager to learn and explore new challenges in the
            tech world.
          </p>

          {/* Social Icons */}
          <div className="social-icons">
            <a
              href="https://www.linkedin.com/in/m-talha-faizan-46158532a/"
              target="_blank"
              rel="noopener noreferrer"
            >
              <FaLinkedin />
            </a>
            <a
              href="https://github.com/talha-xml"
              target="_blank"
              rel="noopener noreferrer"
            >
              <FaGithub />
            </a>
            <a
              href="https://www.instagram.com/not.mtf/"
              target="_blank"
              rel="noopener noreferrer"
            >
              <FaInstagram />
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
