import React, { useState, useEffect } from "react";
import "../styles/Home.css";
import { FaLinkedin, FaGithub, FaInstagram } from "react-icons/fa";
import { Link } from "react-router-dom";

const Home = () => {
  const [stockData, setStockData] = useState({
    symbol: "AAPL",
    price: "--",
    change: "--",
    changePercent: "--",
  });

  const [currencyData, setCurrencyData] = useState({
    base: "EUR",
    rates: { USD: "--", GBP: "--", PKR: "--" },
  });

  // Example users (dummy data)
  const [users] = useState([
    { id: 1, username: "--", state: "--" },
    { id: 2, username: "--", state: "--" },
    { id: 3, username: "--", state: "--" },
  ]);

  // ---------- NEW: login info + activity status ----------
  // read the username from localStorage (set this when login succeeds)
  const [username, setUsername] = useState(() => {
    return localStorage.getItem("username") || null;
  });

  // "Active" or "Idle"
  const [status, setStatus] = useState("Active");

  // keep username in sync if another tab changes it (optional but handy)
  useEffect(() => {
    const onStorage = (e) => {
      if (e.key === "username") {
        setUsername(e.newValue || null);
      }
    };
    window.addEventListener("storage", onStorage);
    return () => window.removeEventListener("storage", onStorage);
  }, []);

  // Idle detection: mark Idle after 5s of no mouse/key/touch activity
  useEffect(() => {
    if (!username) {
      // if nobody is logged in, ensure status is not shown / no listeners
      setStatus("Inactive");
      return;
    }

    let timer = null;

    const markIdle = () => setStatus("Idle");

    const handleActivity = () => {
      setStatus("Active");
      if (timer) clearTimeout(timer);
      timer = setTimeout(markIdle, 5000); // 5 seconds -> Idle
    };

    // listen to a few common events
    window.addEventListener("mousemove", handleActivity);
    window.addEventListener("keydown", handleActivity);
    window.addEventListener("mousedown", handleActivity);
    window.addEventListener("touchstart", handleActivity);

    // start the timer right away
    handleActivity();

    return () => {
      window.removeEventListener("mousemove", handleActivity);
      window.removeEventListener("keydown", handleActivity);
      window.removeEventListener("mousedown", handleActivity);
      window.removeEventListener("touchstart", handleActivity);
      if (timer) clearTimeout(timer);
    };
  }, [username]);
  // ---------- END NEW ----------

  useEffect(() => {
    Promise.all([
      fetch("http://127.0.0.1:5000/api/stock").then((res) => res.json()),
      fetch("http://127.0.0.1:5000/api/currency").then((res) => res.json()),
    ])
      .then(([stockRes, currencyRes]) => {
        if (stockRes) setStockData(stockRes);

        if (currencyRes && currencyRes.rates) {
          setCurrencyData({
            base: currencyRes.base,
            rates: {
              USD: currencyRes.rates.USD,
              GBP: currencyRes.rates.GBP,
              PKR: currencyRes.rates.PKR,
            },
          });
        }
      })
      .catch((err) => console.error("Error fetching data:", err));
  }, []);

  return (
    <div className="home-container">
      {/* ---------- NEW: User Status (top-left) ---------- */}
      {username && (
        <div className="user-status" aria-live="polite">
          <p><strong>Username:</strong> {username}</p>
          <p>
            <strong>Status:</strong>{" "}
            <span className={status === "Active" ? "status-active" : "status-idle"}>
              {status === "Inactive" ? "Not Logged In" : status}
            </span>
          </p>
        </div>
      )}
      {/* ------------------------------------------------- */}

      {/* Hero Section */}
      <div className="hero">
        <div className="hero-left">
          <img
            src="https://via.placeholder.com/250x250.png?text=Profile+Photo"
            alt=""
            className="profile-img"
          />
        </div>

        <div className="hero-right">
          <h3>Hello, Myself</h3>
          <h1>M. Talha Faizan</h1>
          <h2>
            And I'm a{" "}
            <span className="highlight">Full Stack Developer and ML Engineer</span>
          </h2>
          <p>
            I am a passionate Web Developer and have a keen interest in AI/ML
            Engineering. I love building creative and efficient solutions that
            bridge the gap between modern web technologies and intelligent
            systems. Always eager to learn and explore new challenges in the
            tech world.
          </p>

          {/* Social Icons + Login Button */}
          <div className="social-icons">
            <a
              href="https://www.linkedin.com/in/m-talha-faizan-46158532a/"
              target="_blank"
              rel="noopener noreferrer"
            >
              <FaLinkedin />
            </a>
            <a
              href="https://github.com/talha-xml"
              target="_blank"
              rel="noopener noreferrer"
            >
              <FaGithub />
            </a>
            <a
              href="https://www.instagram.com/not.mtf/"
              target="_blank"
              rel="noopener noreferrer"
            >
              <FaInstagram />
            </a>

            <Link to="/login" className="btn login-btn">
              Login
            </Link>
          </div>
        </div>
      </div>

      {/* API Dashboards */}
      <div className="dashboard-container">
        <div className="dashboard-section">
          <h2>Stock Market</h2>
          <div className="card">
            <p>
              <strong>Symbol:</strong> {stockData.symbol || "--"}
            </p>
            <p>
              <strong>Price:</strong> {stockData.price || "--"}
            </p>
            <p>
              <strong>Change:</strong>{" "}
              {stockData.change && stockData.changePercent
                ? `${stockData.change} (${stockData.changePercent})`
                : "--"}
            </p>
          </div>
        </div>

        <div className="dashboard-section">
          <h2>Currency Rates (Base: {currencyData.base || "--"})</h2>
