import React, { useContext, useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";  
import "../styles/Profile.css";
import "../styles/Home.css"; // Import Home.css for dashboards styling

function Profile() {
  const navigate = useNavigate();
  const { user, logout } = useContext(AuthContext);

  // ✅ HOOKS must be declared at the top always
  const [stockData, setStockData] = useState({
    symbol: "AAPL",
    price: "--",
    change: "--",
    changePercent: "--",
  });

  const [currencyData, setCurrencyData] = useState({
    base: "EUR",
    rates: { USD: "--", GBP: "--", PKR: "--" },
  });

  const [status, setStatus] = useState("Active"); // user status
  const idleTimer = useRef(null);

  // ✅ Activity tracker effect
  useEffect(() => {
    const resetTimer = () => {
      setStatus("Active");
      if (idleTimer.current) clearTimeout(idleTimer.current);
      idleTimer.current = setTimeout(() => {
        setStatus("Idle");
      }, 5000);
    };

    window.addEventListener("mousemove", resetTimer);
    window.addEventListener("keydown", resetTimer);

    resetTimer();

    return () => {
      window.removeEventListener("mousemove", resetTimer);
      window.removeEventListener("keydown", resetTimer);
      if (idleTimer.current) clearTimeout(idleTimer.current);
    };
  }, []);

  // ✅ API data fetch
  useEffect(() => {
    Promise.all([
      fetch("http://127.0.0.1:5000/api/stock").then((res) => res.json()),
      fetch("http://127.0.0.1:5000/api/currency").then((res) => res.json()),
    ])
      .then(([stockRes, currencyRes]) => {
        if (stockRes) setStockData(stockRes);

        if (currencyRes && currencyRes.rates) {
          setCurrencyData({
            base: currencyRes.base,
            rates: {
              USD: currencyRes.rates.USD,
              GBP: currencyRes.rates.GBP,
              PKR: currencyRes.rates.PKR,
            },
          });
        }
      })
      .catch((err) => console.error("Error fetching data:", err));
  }, []);

  // ✅ Safe early navigation after hooks
  useEffect(() => {
    if (!user) {
      navigate("/login");
    }
  }, [user, navigate]);

  if (!user) {
    return <p>Loading...</p>; // show loader while redirecting
  }

  // ✅ Safe to use user now
  const firstName = user.full_name?.split(" ")[0] || "";
  const formattedFirstName =
    firstName.charAt(0).toUpperCase() + firstName.slice(1);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <div className="profile-page">
      <div className="profile-info">
        <h1>Welcome, {formattedFirstName} 👋</h1>
        <p><strong>Name:</strong> {user.full_name}</p>
        <p><strong>Registered Email:</strong> {user.email}</p>
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>

      <div className="profile-note">
        <p>
          Thank you for creating an account on my portfolio.  
          This functionality is added to provide you with a personalized experience 
          and to showcase how authentication and user management can be implemented 
          in a real-world application.  
        </p>
      </div>

      {/* ================= DASHBOARDS ================= */}
      <div className="dashboard-container">
        <div className="dashboard-section">
          <h2>Stock Market</h2>
          <div className="card">
            <p><strong>Symbol:</strong> {stockData.symbol || "--"}</p>
            <p><strong>Price:</strong> {stockData.price || "--"}</p>
            <p>
              <strong>Change:</strong>{" "}
              {stockData.change && stockData.changePercent
                ? `${stockData.change} (${stockData.changePercent})`
                : "--"}
            </p>
          </div>
        </div>

        <div className="dashboard-section">
          <h2>Currency Rates (Base: {currencyData.base || "--"})</h2>
          <div className="card">
            <p><strong>USD:</strong> {currencyData.rates.USD || "--"}</p>
            <p><strong>GBP:</strong> {currencyData.rates.GBP || "--"}</p>
            <p><strong>PKR:</strong> {currencyData.rates.PKR || "--"}</p>
          </div>
        </div>
      </div>

      {/* ================= USER ACTIVITY ================= */}
      <div className="table-container">
        <div className="card">
          <h2>User Activity</h2>
          <table className="user-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{user.full_name}</td>
                <td
                  style={{
                    color: status === "Active" ? "green" : "goldenrod",
                    fontWeight: "bold",
                  }}
                >
                  {status}
                </td>
              </tr>
            </tbody>
          </table>

          <h2 className="table-margin-top">Application Load & Stress</h2>
          <table className="user-table">
            <thead>
              <tr>
                <th>Total Users</th>
                <th>Active Users</th>
                <th>Load</th>
                <th>Stress (%)</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>--</td>
                <td>--</td>
                <td>--</td>
                <td>--</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Profile;
