import "../styles/Profile.css";
function Profile() {
  const navigate = useNavigate();
  const { user, logout } = useContext(AuthContext);  // âœ… use context logout

  if (!user) {
    navigate("/login");
    return null;
  }

  const handleLogout = () => {
    logout();   // âœ… clears context + localStorage
    navigate("/login"); // âœ… redirect after logout
  };

  return (
