import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../styles/Home.css";

function Home() {
  const [stockData, setStockData] = useState({
    symbol: "AAPL",
    price: "—",
    change: "—",
    changePercent: "—",
  });

  const [currencyData, setCurrencyData] = useState({
    base: "EUR",
    rates: { USD: "—", GBP: "—", PKR: "—" },
  });

  // Fetch currency rates
  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/currency")
      .then((res) => res.json())
      .then((data) => {
        if (data && data.rates) {
          setCurrencyData({
            base: data.base,
            rates: {
              USD: data.rates.USD,
              GBP: data.rates.GBP,
              PKR: data.rates.PKR,
            },
          });
        }
      })
      .catch((err) => console.error("Error fetching currency:", err));
  }, []);

  // Fetch stock data
  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/stock") // Flask stock endpoint
      .then((res) => res.json())
      .then((data) => setStockData(data))
      .catch((err) => console.error("Error fetching stock:", err));
  }, []);

  return (
    <div className="home-page">
      <h1 className="home-welcome">Welcome to My Portfolio</h1>

      <div className="home-main">
        {/* LEFT SIDE */}
        <div className="home-left">
          {/* Warning first */}
          <p className="home-info">
            ⚠️ This portfolio is to showcase my skills, and Login/Signup
            functionality and API fetching is totally for my Fullstack practice.{" "}
            <span className="warning">No need to create an account.</span>
          </p>

          {/* Buttons below */}
          <div className="home-buttons">
            <Link to="/login" className="home-btn">Login</Link>
            <Link to="/signup" className="home-btn">Sign Up</Link>
          </div>
        </div>

        {/* RIGHT SIDE */}
        <div className="home-right">
          <div className="dashboard-row">
            {/* Stock Card */}
            <div className="dashboard-section">
              <h2>Stock Market</h2>
              <div className="card">
                <p><strong>Symbol:</strong> {stockData.symbol}</p>
                <p><strong>Price:</strong> {stockData.price}</p>
                <p>
                  <strong>Change:</strong> {stockData.change} (
                  {stockData.changePercent})
                </p>
              </div>
            </div>

            {/* Currency Card */}
            <div className="dashboard-section">
              <h2>Currency Rates (Base: {currencyData.base})</h2>
              <div className="card">
                <p><strong>USD:</strong> {currencyData.rates.USD}</p>
                <p><strong>GBP:</strong> {currencyData.rates.GBP}</p>
                <p><strong>PKR:</strong> {currencyData.rates.PKR}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
