import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Signup.css";

function Signup() {
  const [passwordVisible, setPasswordVisible] = useState(false);
  const [confirmPasswordVisible, setConfirmPasswordVisible] = useState(false);
  const [error, setError] = useState("");

  const togglePasswordVisibility = () => {
    setPasswordVisible(!passwordVisible);
  };

  const toggleConfirmPasswordVisibility = () => {
    setConfirmPasswordVisible(!confirmPasswordVisible);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Grab values directly from the form
    const fullName = e.target.fullName.value.trim();
    const username = e.target.username.value.trim();
    const email = e.target.email.value.trim();
    const password = e.target.password.value.trim();
    const confirmPassword = e.target.confirmPassword.value.trim();

    if (!fullName || !username || !email || !password || !confirmPassword) {
      setError("Please fill in all fields.");
      return;
    }

    if (password !== confirmPassword) {
      setError("Passwords do not match.");
      return;
    }

    setError(""); // clear error if everything is fine

    // later you’ll replace this with API call
    console.log("Form submitted:", { fullName, username, email, password });
  };

  return (
    <div className="signup-page">
      <div className="signup-container">
        <h1>Sign Up</h1>

        {/* Info Card */}
        <div className="signup-info-card">
          ⚠️ No need to create an account! This is only for Fullstack practice purposes.
        </div>

        {/* Error Message */}
        {error && <div className="signup-error">{error}</div>}

        <form className="signup-form" onSubmit={handleSubmit}>
          <input
            type="text"
            name="fullName"
            placeholder="Full Name"
            className="signup-input"
          />
          <input
            type="text"
            name="username"
            placeholder="Username"
            className="signup-input"
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            className="signup-input"
          />

          {/* Password Field */}
          <div className="password-wrapper">
            <input
              type={passwordVisible ? "text" : "password"}
              name="password"
              placeholder="Password"
              className="signup-input password-input"
            />
            <button
              type="button"
              className="toggle-password-btn"
              onClick={togglePasswordVisibility}
            >
              {passwordVisible ? "Hide" : "Show"}
            </button>
          </div>

          {/* Confirm Password Field */}
          <div className="password-wrapper">
            <input
              type={confirmPasswordVisible ? "text" : "password"}
              name="confirmPassword"
              placeholder="Confirm Password"
              className="signup-input password-input"
            />
            <button
              type="button"
              className="toggle-password-btn"
              onClick={toggleConfirmPasswordVisibility}
            >
              {confirmPasswordVisible ? "Hide" : "Show"}
            </button>
          </div>

          <button className="signup-btn" type="submit">
            Create Account
          </button>
        </form>

        <p className="signup-login-text">
          Already a member?{" "}
          <Link to="/login" className="signup-login-link">
            <u>Login Here</u>
          </Link>
        </p>
      </div>
    </div>
  );
}

export default Signup;
